function init() {

    let _C = {
      XWID:250,
      YHT: 122,
    };
    let mx = _C.XWID/4-4; //center
    let my = _C.YHT/2;
  
    let drawClock = (d) => {
      g.setBgColor(-1).setColor(0);
      g.clearRect(0,0,mx*2, my*2);
      let hrAngle = (d.hr * 60 + d.min) * Math.PI / 360;
      let minAngle = d.min / 30 * Math.PI;
  
      g.drawImage(_S.read("minute.png"), mx, my, { rotate: minAngle, scale: 0.4 });
      g.drawImage(_S.read("hour.png"), mx, my, { rotate: hrAngle, scale: 0.4 });
      //g.setColor("#FFFFFF").fillCircle(72, 70, 5);
      g.fillCircle(mx, my, 3);
      g.drawCircle(mx,my, my*0.875);
      //g.drawString(`The time is ${d.hr}:${d.min}`, mx, (my*2)-12);
      g.flip();
    };
    
    let drawData = (d) => {
    };
  
    let showMsg = (msgobj) => {  
      let y = 0;
      //let cx = mx + 8;
      g.clearRect(_C.XWID/2, y,_C.XWID-1,_C.YHT-1);
      if(msgobj.title) { 
        g.setFontAlign(0,-1);
        g.fillRect(_C.XWID/2, y, _C.XWID-1, y+g.getFontHeight()+2);
        g.setColor(-1);
        g.drawString(msgobj.title, _C.XWID*0.75, y+2);
        g.setColor(0);
        y+=g.getFontHeight()+2;
      }
      g.setFontAlign(-1,-1);
      let mstr = ''; 
      y+= 8;
      msgobj.text.split(' ').forEach((w)=>{
        if(g.stringWidth(mstr+w) > 120 || w == '|') {
          g.drawString(mstr, _C.XWID*0.5, y);
          mstr='';
          y+=g.getFontHeight();
        }
        if(w != '|') mstr += w + ' ';
      });
      g.drawString(mstr, _C.XWID*0.5, y);
      g.flip();
    };
  
    return {
      start: function () { g.clear(); },
      drawClock: drawClock,
      drawData: drawData,
      showMsg: showMsg,
    };
  }
  exports = init();
  
