    function go() {
      //let R=Math.random, C=Math.cos, S=Math.sin;
      let R=Math.random() > 0.5 ? 1 : 0;
      let G=Math.random() > 0.5 ? 1 : 0;
      let B=Math.random() > 0.5 ? 1 : 0;
      g.clear();
    
      for(let a=0; a < 20; a+=2) {
        g.setColor(R*a*12/256, G*a*12/256, B*a*12/256);
        g.drawCircle(120, 120, 119-a);
      }
      let dt=Date();
      drawTime(dt);
      drawDate(dt);
    
    }
    
    function drawTime(dt) {
      let hr = dt.getHours();
      let min = dt.getMinutes();
      g.drawImage(getDigit(Math.floor(hr/10)), 30, 92);
      g.drawImage(getDigit(hr % 10), 70, 92);
      g.drawImage(getDigit(Math.floor(min/10)), 120, 92);
      g.drawImage(getDigit(min%10), 160, 92);
    }
    function drawDate(dt) {
      let mon = dt.getMonth() + 1;
      let day = dt.getDate();
      g.drawImage(getDigit(Math.floor(mon/10)), 72, 150, {scale: 0.5});
      g.drawImage(getDigit(mon % 10), 92, 150, {scale: 0.5});
      g.drawImage(getDigit(Math.floor(day/10)), 122, 150, {scale: 0.5});
      g.drawImage(getDigit(day%10), 142, 150, {scale: 0.5});
    }
    
