/* 

Originally copied from the OmniGo 100 GEOS powered PDA

This version has cleaned up edges from the original

Usage:

```
require("FontOmnigo").add(Graphics);
g.setFontOmnigo();
g.drawString("0123456789");
```
*/
exports.add = function(graphics) {
  graphics.prototype.setFontOmnigo = function(scale) {
    this.setFontCustom(atob(''), 32, atob('AwIEBgYIBgIEBAUGAwUDBAYEBgYGBgYGBgYEBQYFBgYLBwcHBwcGBwcEBQcGCAcHBwcHBgYHBwkHBwcDBAMHBwMGBgYGBgUGBgIEBgIIBgYGBgQFBAYGCAYGBQQCBAc='), 13 + (scale << 8) | 256);
  }
}
